declare class MarkovData {
    finalData: Record<string, string[]>;
    startData: string[];
    endDelimiter: string;
    forbidden: string[];
    constructor(data: string[]);
    private _createData;
    getStart(): string;
    getNext(current: string): string;
    add(data: string): Promise<void>;
}

interface MarkovOptions {
    data: MarkovData;
    maxLength?: number;
}
declare class Markov {
    static generate(options: MarkovOptions): string;
    static choose(current: string, markovData: MarkovData, sequence: string, maxLength: number): string;
    static complete(options: MarkovOptions & {
        start: string;
    }): string;
}

export { Markov, MarkovData, MarkovOptions };
